import React, { useState } from "react";

import './ExpenseForm.css';

const ExpenseForm = () => {
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState(0);

    const [userInput, setUserInput] = useState({
        enteredTitle: '',
        enteredAmount: '',
        enteredDate: '',
    });
    const titleChangeHandler = (event) => {
        console.log(event.target.value);
        setUserInput({
            ...userInput,
            enteredTitle: event.target.value,
        })
    }
    const amountChangeHandler = (event) => {
        setEnteredAmount(event.target.value)
        console.log(event.target.value);
    }
    const dateChangeHandler = (event) =>{
        console.log(event.target.value);
    }
    return (
        <form>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input id="titleVal" onChange={titleChangeHandler} type="text"></input>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input onChange={amountChangeHandler} type="number" min="0.01" step="0.01"></input>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input onChange={dateChangeHandler} type="date" min="2000-01-01" max="3000-01-01"></input>
                </div>
            </div>
            <div>
                <div>Title: {title}</div>
                <div>Amount: {EnteredAmount}</div>
            </div>
            <button type="submit">Add Expense</button>
        </form>
    )
}

export default ExpenseForm;
