import React, { useState } from "react";

const Product = (props) => {
    const [categoryType, setCategory] = useState(props.type);

    
    const products = props.items.filter((product) => {
        return product.category == categoryType;
    });
        

    const productssd = props.items.filter((product) => (
        <>
            <div>{product.price} - {product.category} - {product.name} </div>
            <button type="submit">Delete</button>
        </>
    ));

    return products;
};

export default Product;
