import React, { useState } from "react";
import Product from "./Product";
import ProductFilter from "./ProductFilter";

const productCategoryType = [
    {
        id: 'elec',
        categoryType: 'Electronic'
    },
    {
        id: 'food',
        categoryType: 'Food'
    },
    {
        id: 'skin',
        categoryType: 'Skincare'
    }
];

const ProductCategory = (props) => {

    const productFiltered = productCategoryType.map((category) => (
        
        <div key={category.id}>
            <h3>{category.categoryType} Items</h3>
                
        </div>
    ));

    const productCategoryListing = productCategoryType.map((category) => (
        <>
            <h3 key={category.id}>{category.categoryType} Items</h3>
            <ul className='product-list'>
                <li>
                    <ProductFilter items={props.tt} type={category.categoryType}></ProductFilter>
                </li>
            </ul>
        </>
    ));

    return (
        <>        
            {productCategoryListing}
        </>
    )
};

export default ProductCategory;
