import React, { useState } from "react";

const ProductForm = (props) => {
    const [productId, setProductId] = useState(Math.random().toString());
    const [productName, setProductName] = useState('');
    const [productPrice, setProductPrice] = useState(0);
    const [productCategory, setProductCategory] = useState('Electronic');

    return (
            <form>
                <div>
                <label>id</label>
                <input type="text" value={productId}></input>
                </div>
                <div>
                <label>Product Name</label>
                <input type="text" value={productName}></input>
                </div>
                <div>
                <label>Product Price</label>
                <input type="number" value={productPrice}></input>
                </div>
                <div>
                <label>Product Category</label>
                <select onSelect={productCategory} onChange={productChangeHandler}>
                    <option value="Electronic">Electronics</option>
                    <option value="Food">Food</option>
                    <option value="Skincare">Skincare</option>
                </select>
                </div>
                <button type="submit">Add Product</button>
            </form>
    )
};

export default ProductForm;
