import React, { useState } from "react";

import './UserForm.css';
import Button from "../UI/Button";

const UserForm = (props) => {
    const [enteredUserName, setUserName] = useState('');
    const [enteredUserAge, setUserAge] = useState('');
    const [isValid, setIsValid] = useState(true);
    

    const userNameChangeHandler = (event) =>{
        setUserName(event.target.value);
        setIsValid(true);
    }

    const userAgeChangeHandler = (event) =>{
        setUserAge(event.target.value);
        setIsValid(true);
    }


    const onSubmitHandler = (event) => {
        event.preventDefault();
        if(enteredUserName = '' || enteredUserAge < 0){
            setIsValid(false);
            return;
        }
        const tt = {
            name: enteredUserName,
            age: enteredUserAge,
        }
        console.log(tt);
        props.onAddUser(tt);
        setUserName('');
        setUserAge('');
    }
    return (
        <form onSubmit={onSubmitHandler} className="user-form__controls">
            <div className="user-form__control">
                <label htmlFor="username">Username</label>
                <input id="username" type="text" value={enteredUserName} onChange={userNameChangeHandler}></input>
            </div>
            <div className="user-form__control">
                <label htmlFor="age">Age</label>
                <input id="age" type="number" value={enteredUserAge} onChange={userAgeChangeHandler} min="18" max="125"></input>
            </div>
            <Button type="submit"> Add User</Button>
        </form>
    )
}

export default UserForm;
